    <Link href="/post?title=NextJs" prefetch={false}>
      <a>Post</a>
    </Link>
    <p>This is the about page</p>
  </div>
);

About.getInitialProps = async ({ req }) => {
  const userAgent = req ? req.headers["user-agent"] : navigator.userAgent;
  console.log(123);
  return { userAgent };
};

export default withLayout(About);
